function decorateTextBlock($textBlock) {
  $textBlock.className = "Theme-Layer-BodyText";
  $textBlock.querySelector("div").className = "Theme-Layer-BodyText--inner";
  $textBlock.querySelectorAll("h3").forEach(($text) => {
    $text.className = "Theme-Layer-BodyText-Heading-Small";
  })
  const overlay = document.createElement('div');
  overlay.classList.add("FullSize", "Theme-Overlay");
  overlay.style.opacity = "0.5";
  $textBlock.append(overlay);
}
function getOuterElements($textBlock) {
  const textBlocksInner = document.createElement('div');
  textBlocksInner.className = "MediaRenderer__textBlocksInner";
  const layout = document.createElement('div');
  layout.classList.add("Layout", "Layer--two");
  const layoutRow = document.createElement('div');
  layoutRow.className = "Layout__row";
  const layoutCol = document.createElement('div');
  layoutCol.classList.add("Layout__col", "Layout__col-lg-6", "Layout__col-lg-right", "Layout__col-md-6", "Layout__col-md-right", "Layout__col-sm-10",
    "Layout__col-sm-center", "Layout__col-xs-12", "Layout__col-xs-left", "Theme-Column");
  layoutCol.appendChild($textBlock);
  layoutRow.appendChild(layoutCol);
  layout.appendChild(layoutRow);
  textBlocksInner.appendChild(layout);
  return textBlocksInner;
}
function decorateImageBlock(block) {
  const canvas = document.createElement('canvas');
  canvas.classList.add("FullSize--fullWidth", "DisplayContainerHeight");
  canvas.setAttribute('data-mediarenderer-canvas', 'true');
  canvas.setAttribute('data-mediarenderer-scaling', '2');
  canvas.setAttribute('data-scaling-mode', 'cover');
  canvas.setAttribute('data-y-position', 'center');
  canvas.setAttribute('data-x-position', 'center');
  canvas.setAttribute('data-mediarenderer-transition', 'true');
  canvas.setAttribute('data-lazyload-container', 'true');
  canvas.setAttribute('data-lazyload-trigger', 'true');
  canvas.innerHTML = canvasInnerHTML;
  block.prepend(canvas);
}
export default function decorate(block) {
  block.classList.add("Layer--one", "Theme-Layer-BackgroundCanvas", "Theme-Item-MediaRenderer");
  block.setAttribute('data-mediarenderer','true');
  block.setAttribute('data-mediarenderer-managed-height','true');

  // DOM structure for text blocks
  const outerDiv = document.createElement('div');
  outerDiv.className = "MediaRenderer__textBlocks";
  // block.querySelectorAll("div:not(:first-child)").forEach(($textBlock) => {
  [...block.children].forEach(($textBlock) => {
    outerDiv.appendChild($textBlock);
    decorateTextBlock($textBlock);
    outerDiv.appendChild(getOuterElements($textBlock));
  })
  block.appendChild(outerDiv);

  decorateImageBlock(block);

  const overlayedcaption = document.createElement('div');
  overlayedcaption.classList.add("Theme-OverlayedCaption", "MediaRenderer__fixedCaption", "MediaRenderer__captionDisplay");
  overlayedcaption.setAttribute('data-mediarenderer-caption-display', 'true');
  const caption = document.createElement('div');
  caption.classList.add("Theme-Caption", "Layout");
  caption.setAttribute('data-mediarenderer-caption-container', 'true');
  overlayedcaption.append(caption);
  document.getElementsByClassName("MediaRenderer__textBlocks")[0].after(overlayedcaption);
}

